import React from 'react'
import { Link, useLocation } from 'react-router-dom'
import Style from "./NavBar.module.css"

const NavBar = () => {
    const location = useLocation();
    const {pathname} = location;
    const splitLocation = pathname.split("/");

    return (
        <div className={splitLocation[1] === "home" ? Style.navBar_container : Style.navBar_containerOthers}>
            <div className={splitLocation[1] === "home" ? Style.nav : Style.navOthers}>
                <Link to="/" className={splitLocation[1] === "home" ? Style.menuItem : Style.menuItemOthers}> Inicio </Link>
                <Link to="/aboutMe" className={splitLocation[1] === "home" ? Style.menuItem : Style.menuItemOthers}> Sobre mi </Link>
                <Link to="/portafolio" className={splitLocation[1] === "home" ? Style.menuItem : Style.menuItemOthers}> Portafolio </Link>
                <Link to="/contactMe" className={splitLocation[1] === "home" ? Style.menuItem : Style.menuItemOthers}> Contacto </Link>
            </div>
        </div>
    )
}

export default NavBar
