import React from 'react'
import { Link, useLocation } from 'react-router-dom'
import Style from "./NavBar.module.css"
import Dropdown from "./Dropdown"

const NavBar = () => {
    const location = useLocation();
    const {pathname} = location;
    const splitLocation = pathname.split("/");

    return (
        <div className={splitLocation[1] === "homee" ? Style.navBar_container : Style.navBar_container}>
            <div className={splitLocation[1] === "homee" ? Style.nav : Style.navOthers}>
                <Link to="/" className={splitLocation[1] === "homee" ? Style.menuItem : Style.menuItemOthers}> Inicio </Link>
                <Dropdown />
                <Link to="/aboutMe" className={splitLocation[1] === "homee" ? Style.menuItem : Style.menuItemOthers}> Sobre mi </Link>
                <Link to="/contactMe" className={splitLocation[1] === "homee" ? Style.menuItem : Style.menuItemOthers}> Contacto </Link>
            </div>
        </div>
    )
}

export default NavBar
